 module.exports = function(app) {
   var url=require('url');
   var path=require('path');
   var _documentRoot={ root: path.resolve(__dirname, '..', '..', 'client', app.get('production')?'dist':'app') };

    [
      'bower_components/leaflet/dist/images'

    ].forEach(function(folder){
      app.all('/'+folder+'/*', function(req,res,next){

        /* TODO: get rid workaround below for leaflet.markercluster or angularjs bug where, with html5mode
        enabled and you click on a cluster, inline image is catenated to image
        directory url and merker icons are not displayed. eg: a="https://www.doxel.org/bower_components/leaflet/dist/images/data:image/png;base64,"
"https://www.doxel.org/bower_components/leaflet/dist/images/data:image/png;bâ€¦du5HG6MdHjNcNYGqfDm5YRzLBBCCDl/2bk8a8gdbqcfwECu62Fg/HrggAAAABJRU5ErkJggg==" */
      if (req.url.search('data:image')>0) {
//        console.log(req.url.length)
        switch(req.url.length) {
          case 2392:
            req.url='/bower_components/leaflet/dist/images/marker-icon.png';
            break;
          case 1124:
            req.url='/bower_components/leaflet/dist/images/marker-shadow.png';
            break;
          }
        }

        res.sendFile(url.parse(req.url).pathname, _documentRoot);
      });
    });

}
